import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Container,
  Grid,
  Paper,
  Chip,
  LinearProgress,
  Card,
  CardContent,
  CardHeader,
  IconButton,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  Alert,
} from '@mui/material';
import {
  Memory as MemoryIcon,
  Storage as StorageIcon,
  Speed as SpeedIcon,
  Warning as WarningIcon,
  CheckCircle as CheckCircleIcon,
  Error as ErrorIcon,
  Refresh as RefreshIcon,
  Group as GroupIcon,
  VpnKey as VpnKeyIcon,
} from '@mui/icons-material';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import { SystemMetrics, Alert as AlertType } from '../types/api';
import { metricsApi, alertsApi } from '../services/api';

// Chart.jsの設定
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = () => {
  const [metrics, setMetrics] = useState<SystemMetrics | null>(null);
  const [alerts, setAlerts] = useState<AlertType[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchData = async () => {
    try {
      setLoading(true);
      const [metricsData, alertsData] = await Promise.all([
        metricsApi.getSystemMetrics(),
        alertsApi.getAlerts()
      ]);
      
      if (metricsData.status === 'success' && metricsData.data) {
        setMetrics(metricsData.data);
      }
      
      if (alertsData.status === 'success' && alertsData.data) {
        setAlerts(alertsData.data);
      }
    } catch (err) {
      setError('データの取得に失敗しました');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
    // 5分ごとに更新
    const interval = setInterval(fetchData, 5 * 60 * 1000);
    return () => clearInterval(interval);
  }, []);

  const getStatusColor = (value: number): 'success' | 'warning' | 'error' => {
    if (value < 70) return 'success';
    if (value < 90) return 'warning';
    return 'error';
  };

  const formatBytes = (bytes: number): string => {
    const gb = bytes / (1024 * 1024 * 1024);
    return `${gb.toFixed(1)} GB`;
  };

  return (
    <Container maxWidth="lg">
      <Box sx={{ my: 4 }}>
        <Box display="flex" justifyContent="space-between" alignItems="center" mb={4}>
          <Typography variant="h4" component="h1">
            IT運用システム ダッシュボード
          </Typography>
          <IconButton onClick={fetchData} disabled={loading}>
            <RefreshIcon />
          </IconButton>
        </Box>

        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
        )}

        {loading ? (
          <LinearProgress sx={{ mb: 3 }} />
        ) : (
          <Grid container spacing={3}>
            {/* システムリソース */}
            <Grid item xs={12} md={8}>
              <Card>
                <CardHeader title="システムリソース" />
                <CardContent>
                  <Grid container spacing={2}>
                    <Grid item xs={12} sm={4}>
                      <Box textAlign="center">
                        <SpeedIcon sx={{ fontSize: 40, color: 'primary.main', mb: 1 }} />
                        <Typography variant="h6">CPU使用率</Typography>
                        <LinearProgress
                          variant="determinate"
                          value={metrics?.cpu.usage || 0}
                          color={getStatusColor(metrics?.cpu.usage || 0)}
                          sx={{ height: 10, borderRadius: 5, my: 1 }}
                        />
                        <Typography variant="body2" color="text.secondary">
                          {metrics?.cpu.usage}% ({metrics?.cpu.temperature}°C)
                        </Typography>
                      </Box>
                    </Grid>

                    <Grid item xs={12} sm={4}>
                      <Box textAlign="center">
                        <MemoryIcon sx={{ fontSize: 40, color: 'primary.main', mb: 1 }} />
                        <Typography variant="h6">メモリ使用率</Typography>
                        <LinearProgress
                          variant="determinate"
                          value={(metrics?.memory.used / metrics?.memory.total) * 100 || 0}
                          color={getStatusColor((metrics?.memory.used / metrics?.memory.total) * 100 || 0)}
                          sx={{ height: 10, borderRadius: 5, my: 1 }}
                        />
                        <Typography variant="body2" color="text.secondary">
                          {formatBytes(metrics?.memory.used || 0)} / {formatBytes(metrics?.memory.total || 0)}
                        </Typography>
                      </Box>
                    </Grid>

                    <Grid item xs={12} sm={4}>
                      <Box textAlign="center">
                        <StorageIcon sx={{ fontSize: 40, color: 'primary.main', mb: 1 }} />
                        <Typography variant="h6">ディスク使用率</Typography>
                        <LinearProgress
                          variant="determinate"
                          value={(metrics?.disk.used / metrics?.disk.total) * 100 || 0}
                          color={getStatusColor((metrics?.disk.used / metrics?.disk.total) * 100 || 0)}
                          sx={{ height: 10, borderRadius: 5, my: 1 }}
                        />
                        <Typography variant="body2" color="text.secondary">
                          {formatBytes(metrics?.disk.used || 0)} / {formatBytes(metrics?.disk.total || 0)}
                        </Typography>
                      </Box>
                    </Grid>
                  </Grid>
                </CardContent>
              </Card>
            </Grid>

            {/* システムステータス */}
            <Grid item xs={12} md={4}>
              <Card>
                <CardHeader title="システムステータス" />
                <CardContent>
                  <List>
                    <ListItem>
                      <ListItemIcon>
                        <VpnKeyIcon color="primary" />
                      </ListItemIcon>
                      <ListItemText
                        primary="認証サービス"
                        secondary={
                          <Chip
                            size="small"
                            color="success"
                            label="正常"
                            sx={{ mt: 0.5 }}
                          />
                        }
                      />
                    </ListItem>
                    <Divider />
                    <ListItem>
                      <ListItemIcon>
                        <GroupIcon color="primary" />
                      </ListItemIcon>
                      <ListItemText
                        primary="Active Directory"
                        secondary={
                          <Chip
                            size="small"
                            color="success"
                            label="正常"
                            sx={{ mt: 0.5 }}
                          />
                        }
                      />
                    </ListItem>
                  </List>
                </CardContent>
              </Card>
            </Grid>

            {/* アラート一覧 */}
            <Grid item xs={12}>
              <Card>
                <CardHeader 
                  title="最新アラート" 
                  action={
                    <Chip
                      label={`${alerts.length} 件`}
                      color={alerts.some(a => a.type === 'critical') ? 'error' : 'default'}
                    />
                  }
                />
                <CardContent>
                  <List>
                    {alerts.slice(0, 5).map((alert) => (
                      <ListItem key={alert.id}>
                        <ListItemIcon>
                          {alert.type === 'critical' ? (
                            <ErrorIcon color="error" />
                          ) : alert.type === 'warning' ? (
                            <WarningIcon color="warning" />
                          ) : (
                            <CheckCircleIcon color="success" />
                          )}
                        </ListItemIcon>
                        <ListItemText
                          primary={alert.message}
                          secondary={new Date(alert.timestamp).toLocaleString()}
                        />
                        {!alert.acknowledged && (
                          <Chip
                            label="未確認"
                            color="warning"
                            size="small"
                            sx={{ ml: 1 }}
                          />
                        )}
                      </ListItem>
                    ))}
                  </List>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        )}
      </Box>
    </Container>
  );
};

export default Dashboard;
